		ИНТЕРНЕТ СЕРВИС ПОТОКОВОГО АУДИО 
		Audio Streaming Internet Service
		
	База данных содержит структуру сервиса потокового аудио 
	(по типу Spotify, Яндекс.Музыка, MTC Music) 
	и хранимую в ней информацию  
*/

/* 2. минимальное количество таблиц - 10;

		1. ПОЛЬЗОВАТЕЛИ 	
		2. МУЗЫКАЛЬНЫЙ ЖАНР 		
		3. ИСПОЛНИТЕЛИ			
		4. АЛЬБОМЫ			
		5. КОМПОЗИЦИИ			
		6. ПЛЕЙЛИСТЫ 		
		7. СОДЕРЖИМОЕ ПЛЕЙЛИСТА			
		8. МЕДИАТЕКА		
		9. ЛЮБИМЫЕ ТРЕКИ			
		10. ПОДПИСЧИКИ
*/		
		
/* 3. скрипты создания структуры БД (с первичными ключами, индексами,
внешними ключами);*/

DROP DATABASE IF EXISTS audio;
CREATE DATABASE audio;
USE audio;

-- ПОЛЬЗОВАТЕЛИ
DROP TABLE IF EXISTS users;
CREATE TABLE users (
	id SERIAL PRIMARY KEY,
    firstname VARCHAR(255),
    lastname VARCHAR(255),
    birthday_at DATE,
    email VARCHAR(100) UNIQUE,
    phone BIGINT UNIQUE,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
 ) COMMENT = 'Пользователи аудио сервиса';


-- МУЗЫКАЛЬНЫЙ ЖАНР
DROP TABLE IF EXISTS music_genre;
CREATE TABLE music_genre (
	id SERIAL PRIMARY KEY,
    name VARCHAR(255) COMMENT 'Название музыкального жанра'
 );


-- ИСПОЛНИТЕЛИ
DROP TABLE IF EXISTS performers;
CREATE TABLE performers (
	id SERIAL PRIMARY KEY,
    name VARCHAR(255) COMMENT 'Название группы/исполнителя',
    country VARCHAR(255),
    foundation_date YEAR,
    music_genre_id BIGINT UNSIGNED COMMENT 'Ссылка на жанр, в котороми играет группа/исмполнитель',
    
    FOREIGN KEY (music_genre_id) REFERENCES music_genre(id) ON UPDATE CASCADE ON DELETE CASCADE
 );		


-- АЛЬБОМЫ
DROP TABLE IF EXISTS albums;
CREATE TABLE albums (
	id SERIAL PRIMARY KEY,
    name VARCHAR(255) COMMENT 'Название альбома',
    creation_date YEAR,
    music_genre_id BIGINT UNSIGNED COMMENT 'Ссылка на жанр к которому принадлежит альбом',
    performer_id BIGINT UNSIGNED COMMENT 'Ссылка на исполнителя, который записал альбом',
    
    FOREIGN KEY (music_genre_id) REFERENCES music_genre(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (performer_id) REFERENCES performers(id) ON UPDATE CASCADE ON DELETE CASCADE
 );		


-- КОМПОЗИЦИИ
DROP TABLE IF EXISTS music_tracks;
CREATE TABLE music_tracks (
	id SERIAL PRIMARY KEY,
    name VARCHAR(255) COMMENT 'Название композиции/трека',
    music_genre_id BIGINT UNSIGNED COMMENT 'Ссылка на жанр, к которому принадлежит трек',
    performer_id BIGINT UNSIGNED COMMENT 'Ссылка на исполнителя, который записал трек',
    album_id BIGINT UNSIGNED COMMENT 'Ссылка на альбом, в который входит трек',
    
    FOREIGN KEY (music_genre_id) REFERENCES music_genre(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (performer_id) REFERENCES performers(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (album_id) REFERENCES albums(id) ON UPDATE CASCADE ON DELETE CASCADE
 );	


-- ПЛЕЙЛИСТЫ
DROP TABLE IF EXISTS playlists;
CREATE TABLE playlists(
	id SERIAL PRIMARY KEY,
	name VARCHAR(255) COMMENT 'Название плейлиста'
) COMMENT = 'Плейлисты могут наполняться на основе какого либо музыкального жанра, либо исполнителя, либо определённого тематического набора композиций/треков'; 
	

-- СОДЕРЖИМОЕ ПЛЕЙЛИСТА
DROP TABLE IF EXISTS playlist_content;
CREATE TABLE playlist_content(
	id SERIAL PRIMARY KEY,
	playlist_id BIGINT UNSIGNED NOT NULL, 
    music_genre_id BIGINT UNSIGNED,
    performer_id BIGINT UNSIGNED,
  	music_track_id BIGINT UNSIGNED,
  	
  	FOREIGN KEY (playlist_id) REFERENCES playlists(id) ON UPDATE CASCADE ON DELETE CASCADE,
  	FOREIGN KEY (music_genre_id) REFERENCES music_genre(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (performer_id) REFERENCES performers(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (music_track_id) REFERENCES music_tracks(id) ON UPDATE CASCADE ON DELETE CASCADE
);


-- МЕДИАТЕКА 
DROP TABLE IF EXISTS media_library;
CREATE TABLE media_library(
	id SERIAL PRIMARY KEY,
    user_id BIGINT UNSIGNED,
    playlist_id BIGINT UNSIGNED,
    performer_id BIGINT UNSIGNED,
    album_id BIGINT UNSIGNED,
    
    FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (playlist_id) REFERENCES playlists(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (performer_id) REFERENCES performers(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (album_id) REFERENCES albums(id) ON UPDATE CASCADE ON DELETE CASCADE
    
)COMMENT = 'У каждого пользователя есть медиатека, где хранятся его сохранённые/любимые плейлисты, исполнители и альбомы'; 
    
						
-- ЛЮБИМЫЕ ТРЕКИ 
DROP TABLE IF EXISTS favorite_tracks;
CREATE TABLE favorite_tracks (
	id SERIAL PRIMARY KEY,
	user_id BIGINT UNSIGNED,
	music_track_id BIGINT UNSIGNED,
	
	FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (music_track_id) REFERENCES music_tracks(id) ON UPDATE CASCADE ON DELETE CASCADE
    
 )COMMENT = 'У каждого пользователя есть список его любимых сохранённых треков';
			

-- ПОДПИСЧИКИ			
DROP TABLE IF EXISTS followers;
CREATE TABLE followers (
	follower_user_id BIGINT UNSIGNED NOT NULL,
    target_user_id BIGINT UNSIGNED NOT NULL,   
	
    PRIMARY KEY (follower_user_id, target_user_id),
	FOREIGN KEY (follower_user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY (target_user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE
)COMMENT = 'Каждый пользователь может подписаться на другого';	 
	 
