	a.name AS 'Название альбома',
	mg.name AS 'Музыкальный жанр',
	p.name AS 'Исполнитель',
	a.creation_date AS 'Дата выпуска'
FROM albums a
JOIN music_genre mg ON a.music_genre_id = mg.id
JOIN performers p ON a.performer_id = p.id
GROUP BY a.name
ORDER BY a.name;


-- Выборка 2: Вывести все альбомы, их жанр, группу и год записи. Только с помощью вложенных запросов и начиная с самого раннего альбома.

SELECT 
	a.name AS 'Название альбома',
	(SELECT name FROM music_genre mg WHERE mg.id = a.music_genre_id) AS 'Музыкальный жанр',
	(SELECT name FROM performers p WHERE p.id = a.performer_id) AS 'Исполнитель',
	a.creation_date AS 'Дата выпуска'
FROM albums a
GROUP BY a.name
ORDER BY a.creation_date;


-- Выборка 3: Выведем содержимое медиатеки для пользователя с id = 1

SELECT  
	CONCAT(u.firstname, ' ', u.lastname) AS 'Полльзователь',	
	pl.name AS 'Плейлисты',
	p.name AS 'Исполнители',
	a.name AS 'Альбомы'
FROM media_library ml 
JOIN users u ON ml.user_id = u.id 
LEFT JOIN playlists pl ON ml.playlist_id = pl.id 
LEFT JOIN performers p ON ml.performer_id = p.id 
LEFT JOIN albums a ON ml.album_id = a.id 
WHERE ml.user_id = 1;
	

-- Выборка 4: Узнать из какой страны больше всего исполнителей

SELECT 
	COUNT(*) AS 'Количество исполнителей',
	p.country
FROM performers p
GROUP BY p.country
ORDER BY COUNT(*) DESC;


-- Выборка 5: Вывести все песни одного альбома

SELECT 
	a.name AS 'Название альбома',
	mt.name AS 'Название песни'
FROM albums a
JOIN music_tracks mt ON mt.album_id = a.id
WHERE a.name = 'Master of Puppets';


-- Выборка 6: Вывести группы, которые образовались между 1980-м и 1990-м годами 

SELECT 
	p.name,
	p.foundation_date 
FROM performers p 
WHERE p.foundation_date BETWEEN '1980' AND '1990'
ORDER BY p.foundation_date;


