-- Представление 1: Все композиции и соответствующие страны, в которых они были написаны

CREATE OR REPLACE VIEW country_of_track AS
SELECT 
	mt.name,
	p.country
FROM music_tracks mt
JOIN performers p ON mt.performer_id = p.id;

-- Выведем первые 10 песен в алфовитном порядке, используя представление 'country_of_track'

SELECT * FROM country_of_track
ORDER BY mt.name
LIMIT 10;


-- Представление 2: Все пользователи и количество сохранённых элементов у них в медиатеках,
-- начиная с пользователя с самым большим количеством элементов

CREATE OR REPLACE VIEW count_of_media AS
SELECT 
	u.firstname,
	((COUNT(ml.performer_id)) + (COUNT(ml.playlist_id)) + (COUNT(ml.album_id))) AS cnt
FROM media_library ml
LEFT JOIN users u ON ml.user_id = u.id
GROUP BY u.id
ORDER BY cnt DESC;

SELECT * FROM count_of_media;


