-- Процедура добавления нового плейлиста

DROP PROCEDURE IF EXISTS add_playlist;
DELIMITER //
CREATE PROCEDURE add_playlist(IN name_of_playlist TEXT)
BEGIN 
	INSERT INTO playlists (name)
	VALUES (name_of_playlist);
END//
DELIMITER ;

CALL add_playlist('Nordic & Viking Music');
SELECT name FROM playlists;


-- Функция, советующая слушать определённый плейлист, в зависимости от времени суток

DROP FUNCTION IF EXISTS advice;

DELIMITER //
CREATE FUNCTION advice ()
RETURNS VARCHAR(255) DETERMINISTIC
BEGIN 
	IF CURRENT_TIME() BETWEEN '06:00:00' AND '12:00:00'
		THEN RETURN 'Рекомендуем послушать плейлист \"Sabaton\'s Battle Themes\"';
	ELSEIF CURRENT_TIME() BETWEEN '12:00:00' AND '18:00:00'
		THEN RETURN 'Рекомендуем послушать плейлист \"Cannibal Corpse Greatest Hits\"';
	ELSEIF CURRENT_TIME() BETWEEN '18:00:00' AND '23:59:59'
		THEN RETURN 'Рекомендуем послушать плейлист \"Swedish Melodic Death Metal Tracks\"';
	ELSEIF CURRENT_TIME() BETWEEN '00:00:00' AND '06:00:00'
		THEN RETURN 'Рекомендуем послушать плейлист \"DSBM from Scandinavia\"';
	END IF;
END//
DELIMITER ;

SELECT advice();


-- Триггер 1: если при добавлении нового альбома, альбом с таким названием уже существует, выйдет ошибка

DELIMITER //
DROP TRIGGER IF EXISTS check_album//
CREATE TRIGGER check_album 
BEFORE INSERT 
ON albums FOR EACH ROW
BEGIN
    IF NEW.name IN (SELECT name FROM albums) THEN
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Добавление невозможно! Такой альбом уже существует!';
    END IF;
END//
DELIMITER ;

