-- Триггер 2: после добавления нового пользователя, сохраняет в переменную @total_users количество всех пользователей

DELIMITER //
DROP TRIGGER IF EXISTS show_users_count//
CREATE TRIGGER show_users_count 
AFTER INSERT 
ON users FOR EACH ROW
BEGIN
    SELECT COUNT(*) INTO @total_users FROM users;
END//
DELIMITER ;

